import React, { Component } from 'react';
import { HashRouter as Router, Link } from 'react-router-dom';
import { withRouter } from 'react-router';

import "./leftBar.scss";


class LeftBar extends Component {
    constructor(props) {
        super(props)
        this.state = { active: 0 }
    }

    handleNavClick = (id) => {
        this.setState({ active: id });
        this.props.history.push('/qrcode');
    }
    render() {
        return (
            <Router>
                <ul className="leftBar">
                    <li className={`nav ${this.state.active == 0 ? 'active' : ''}`} onClick={()=>this.handleNavClick(0)}>
                        <Link to="/linklist">任意门</Link>
                    </li>
                    <li className={`nav ${this.state.active == 1 ? 'active' : ''}`} onClick={this.handleNavClick.bind(this, 1)}>
                        <Link to="/qrcode">二维码</Link>
                    </li>
                    <li className={`nav ${this.state.active == 2 ? 'active' : ''}`} onClick={this.handleNavClick.bind(this, 2)}>
                        <Link to="/decode">编解码</Link>
                    </li>
                    <li className={`nav ${this.state.active == 3 ? 'active' : ''}`} onClick={this.handleNavClick.bind(this, 3)}>
                        <Link to="/hotIssue">热门文章</Link>
                    </li>
                </ul>
            </Router>
        )
    }
}

export default withRouter(LeftBar);
