import { Base64 } from 'js-base64';

import React, { Component } from 'react';
import './decode.scss';

class Decode extends Component {
    constructor(props){
        super(props);
        this.state = {leftValue:"请输入..."}
        this.base64Ref = React.createRef();
        this.messageRef = React.createRef();
    }
    decodeBase64 = () =>{
        let result = Base64.encode(this.base64Ref.current.value);
        //this.messageRef.current.value=result;
        this.setState = {leftValue:result};
    }
    render() {
        return <div className="wrapper">
            <div>
                <label htmlFor="source">明文</label>
                <textarea name="message" id="source" cols="30" rows="10" ref={this.base64Ref} defaultValue="请输入"></textarea>
            </div>
            <div>
                <button onClick={this.decodeBase64}>转BASE64</button>
                <button>转明文</button>
            </div>
            <div>
                <label htmlFor="result">BASE64</label>
                <textarea name="base64" id="result" cols="30" rows="10" ref={this.messageRef} defaultValue="请输入"></textarea>
            </div>

        </div>;
    }
}

export default Decode;
