import QRCode from 'qrcode';

import React, { Component } from 'react';
import './qrcode.scss';

class Qrcode extends Component {
    constructor(props) {
        super(props);
        this.canvasRef = React.createRef();
        this.inputRef = React.createRef();
        this.state = { isClick: false }
    }

    genQrcode = () => {
        QRCode.toCanvas(this.canvasRef.current, this.inputRef.current.value)
    }

    handleInputClick = () => {
        this.setState({ isClick: true })
    }

    render() {
        return <div className="qrcode">
            <div className="inputWrapper">
                <input className={`input ${this.state.isClick == false ? "" : "isInput"}`} type="text" ref={this.inputRef} onClick={this.handleInputClick} placeholder="http://" />
                <button className="clickQrBtn" onClick={this.genQrcode}>生成二维码</button>
            </div>
            <div className="output">
                <canvas className="outputQr" ref={this.canvasRef}>QRCode</canvas>
                <span className="creat-code-tip">此处生成二维码</span>
            </div>

        </div>;
    }
}

export default Qrcode;
