import React, { Component } from 'react';
import qrcode from "qrcode";

import './App.scss';
import LinkList from './components/linkList/LinkList.jsx';
import Dictionary from './components/dictionary/Dictionary.jsx';

class App extends Component {
  constructor(props) {
    super(props);
    this.canvasRef = React.createRef();
    this.inputRef = React.createRef();
  }

  genQrcode = () => {
    qrcode.toCanvas(this.canvasRef.current, this.inputRef.current.value)
  }

  render() {
    return (
      <div className="app">
        <div className="content">
          <Dictionary />
          <canvas ref={this.canvasRef}>qrcode</canvas>
          <input type="text" ref={this.inputRef}/>
          <button onClick={this.genQrcode}>生成</button>
        </div>
        <div className="sidebar">
          <LinkList />
        </div>
      </div>
    );
  }
}

export default App;
