import React, { Component } from 'react';
import { HashRouter as Router, Link } from 'react-router-dom';
import { withRouter } from 'react-router';

import "./leftBar.scss";


class LeftBar extends Component {
    constructor(props) {
        super(props)
        const { pathname } = this.props.location;
        this.state = { active: pathname }
    }
    // componentDidMount() {
    //     // this.setState({ active: window.location.hash.replace('#', '') })
    //     const { pathname } = this.props.location;
    //     this.setState({ active: pathname })
    // }

    handleNavClick = (path) => {
        this.setState({ active: path });
        // this.props.history.push('/qrcode');
        this.props.history.push(path);

    }
    render() {
        return (
            <Router>
                <ul className="leftBar">
                    <li className={`nav ${this.state.active == "/linklist" ? 'active' : ''}`} onClick={() => this.handleNavClick("/linklist")}>任意门
                        {/* <Link to="/linklist">任意门</Link> */}
                    </li>
                    <li className={`nav ${this.state.active == "/qrcode" ? 'active' : ''}`} onClick={this.handleNavClick.bind(this, "/qrcode")}>二维码
                        {/* <Link to="/qrcode">二维码</Link> */}
                    </li>
                    <li className={`nav ${this.state.active == "/decode" ? 'active' : ''}`} onClick={this.handleNavClick.bind(this, "/decode")}>编解码
                        {/* <Link to="/decode">编解码</Link> */}
                    </li>
                    <li className={`nav ${this.state.active == "/hotIssue" ? 'active' : ''}`} onClick={this.handleNavClick.bind(this, "/hotIssue")}>热门文章
                        {/* <Link to="/hotIssue">热门文章</Link> */}
                    </li>
                </ul>
            </Router>
        )
    }
}

export default withRouter(LeftBar);
