import React, { Component } from 'react';
// import qrcode from "qrcode";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

import './App.scss';

import LinkList from './components/linkList/LinkList.jsx';
import Qrcode from './components/qrcode/Qrcode.jsx';
import Decode from './components/decode/Decode.jsx';
import Dictionary from './components/dictionary/Dictionary.jsx';
import LeftBar from './components/leftBar/LeftBar.jsx';

class App extends Component {
  // constructor(props) {
  //   super(props);
  //   this.canvasRef = React.createRef();
  //   this.inputRef = React.createRef();
  // }

  // genQrcode = () => {
  //   qrcode.toCanvas(this.canvasRef.current, this.inputRef.current.value)
  // }

  render() {
    return (
      <Router>
      <div className="app">
        <div className="leftBar">
          <LeftBar />
        </div>
      </div>

      <Switch>
        <Route path= "/linklist">
          <LinkList />
        </Route>
        <Route path= "qrcode">
          <Qrcode />
        </Route>
        <Route path= "decode">
          <Decode />
        </Route>
        <Route path= "hotIssue">
          <Decode />
        </Route>
      </Switch>
      </Router>
    );
  }
}

export default App;
