import QRCode from 'qrcode';

import React, { Component } from 'react';
import './qrcode.scss';

class Qrcode extends Component {
    constructor(props) {
        super(props);
        this.canvasRef = React.createRef();
    }

    componentDidMount(){
        QRCode.toCanvas(this.canvasRef.current,"https://www.npmjs.com/package/qrcode")
    }

    render() { 
        return <div>
            <input type="text"/>
            <button>查询</button>
            <canvas ref= {this.canvasRef} >QRCode</canvas>
        </div>;
    }
}

export default Qrcode;
