import React, { Component } from 'react';
import './js-sdk/md5';
import 'http://apps.bdimg.com/libs/jquery/1.9.1/jquery.min.js';

import "./dictionary.scss";

class Dictionary extends Component {
    state = {}
    constructor(props) {
        super(props);
        this.dicInputRef = React.createRef();
        this.dicOutputRef = React.createRef();
    }

    genTranslate = () => {
        var appid = '20200709000516150';
        var key = 'n3Ts5qaI2yDcgZTbvXsV';
        var salt = (new Date).getTime();
        var query = this.dicInputRef.current.value;
        // 多个query可以用\n连接  如 query='apple\norange\nbanana\npear'
        var from = 'zh';
        var to = 'en';
        var str1 = appid + query + salt + key;
        var sign = MD5(str1)
        $.ajax({
            url: 'http://api.fanyi.baidu.com/api/trans/vip/translate',
            type: 'get',
            dataType: 'jsonp',
            data: {
                q: query,
                appid: appid,
                salt: salt,
                from: from,
                to: to,
                sign: sign
            },
            success: function (data) {
                console.log(data);
            }
        });
    }
    render() {
        return <div>
            <input type="text" className="dicInput" ref={this.dicInputRef} placeholder="在此输入要翻译的单词或者文字" />
            <button className="clickDicBtn" onClick={this.genTranslate} >翻译</button>
            <div className="dicOutput" ref={this.dicOutputRef}>

            </div>
        </div>;
    }
}

export default Dictionary;
